const snekfetch = require('snekfetch');

class randomAnimals {

	/**
	 * It returns a random dog
	 */
	async dog() {
		try {
			const { body } = await snekfetch
				.get('https://random.dog/woof.json');
                	return body.url;
		} catch (err) {
			throw 'Error: ' + err.message;
		}
	}

}

module.exports = new randomAnimals();
